define(['jquery'], function($) {
    "use strict";

    $.widget('ui.click', {
        options: {
        },
        _create: function () {
            this.element.on('click', function(event) { alert('Hey! I\'m on teh page!') })
        }
    });

    return {'myClickWidget': $.ui.click};
});

function init(el, config, component) {
    require([component], function (fn) {

        if (typeof fn === 'object') {
            fn = fn[component].bind(fn);
        }

        if (_.isFunction(fn)) {
            fn(config, el);
        } else if ($(el)[component]) {
            $(el)[component](config);
        }
    });
}
