define([
    'underscore',
    'Magento_Checkout/js/model/shipping-rates-validation-rules'
], function (_, Component) {
    "use strict";
    return _.extend({}, Component, {
        getObservableFields: function() {
            return _.union(Component.getObservableFields(), ['street', 'region_id']);
        }
    });
});
